import { html } from '@polymer/polymer/lib/utils/html-tag.js';
import { PolymerElement } from '@polymer/polymer/polymer-element.js';
class ElementBasicDemos extends DemoReadyEventEmitter(ElementDemo(PolymerElement)) {
  static get template() {
    return html`
    <style include="vaadin-component-demo-shared-styles">
      :host {
        display: block;
      }
    </style>

    <h3>Basic Usage</h3>
    The popup will be shown once to every user and use a standard text.
    <vaadin-demo-snippet id="cookie-consent-demo-basic-usage">
      <template preserve-content="">
        <vaadin-cookie-consent></vaadin-cookie-consent>
      </template>
    </vaadin-demo-snippet>

    <h3>Custom Values Example</h3>
    All the texts in the dialog can be customized and localized.
    <vaadin-demo-snippet id="cookie-consent-demo-customizing-texts">
      <template preserve-content="">
        <vaadin-cookie-consent message="We are using cookies to make your visit here awesome!" dismiss="Cool!" learn-more="Why?" learn-more-link="https://vaadin.com/terms-of-service">
        </vaadin-cookie-consent>
      </template>
    </vaadin-demo-snippet>

    <h3>Styling the Content</h3>
    The visual looks can be changed using CSS. The style tag needs to be on the top level of the document, outside all web components.
    <vaadin-demo-snippet id="cookie-consent-demo-theming">
      <template preserve-content="">
        <style>
          div.cc-window.cc-banner {
            background: rgb(136, 212, 254);
          }

          .cc-window .cc-dismiss {
            background: rgb(255, 81, 0);
          }

          .cc-window {
            font-size: 24px;
          }

          .cc-window .cc-message {
            color: rgb(0, 0, 255);
          }

          div.cc-window .cc-message a.cc-link {
            color: rgb(255, 81, 0);
          }
        </style>
        <vaadin-cookie-consent></vaadin-cookie-consent>
      </template>
    </vaadin-demo-snippet>
`;
  }

  static get is() {
    return 'element-basic-demos';
  }

  ready() {
    super.ready();
    this._substituteImg('cookie-consent-demo-basic-usage',
      'https://raw.githubusercontent.com/vaadin/vaadin-cookie-consent/master/demo/images/basic-usage.png');
    this._substituteImg('cookie-consent-demo-customizing-texts',
      'https://raw.githubusercontent.com/vaadin/vaadin-cookie-consent/master/demo/images/customization.png');
    this._substituteImg('cookie-consent-demo-theming',
      'https://raw.githubusercontent.com/vaadin/vaadin-cookie-consent/master/demo/images/theming.png');
  }

  _substituteImg(demoId, imgSrc) {
    var img = document.createElement('img');
    img.src = imgSrc;
    img.style.maxWidth = '100%';

    this.$[demoId].$.demo.innerHTML = '';
    this.$[demoId].$.demo.style.textAlign = 'center';
    this.$[demoId].$.demo.style.lineHeight = 'normal';
    this.$[demoId].$.demo.appendChild(img);
  }
}
customElements.define(ElementBasicDemos.is, ElementBasicDemos);
